import React, { useState } from 'react';
import axios from 'axios';

export default function FormatData() {

  const [quoteNumber, setQuoteNumber] = useState('');

  const [data, setData] = useState(null);

  const [loading, setLoading] = useState(false);

  const handleFetchData = () => {

    console.log("requesting");

    setLoading(true);

    axios
      .get(`http://localhost:9000/aliens/quote/${quoteNumber}`)

      .then(response => {

        if (response.status === 200) {

          setData(response.data);

        } else {

          alert('An error occurred');
        }
      })
      .catch(error => {

        console.log('Error', error);

      })
      .finally(() => {

        setLoading(false);

      });
  };

  return (
    <div className='container'>
      <label className='label' htmlFor='quoteNumber'>
        Quote Number:
      </label>
      <textarea
        type='text'
        value={quoteNumber}
        onChange={e => setQuoteNumber(e.target.value)}
        placeholder='Enter quote number'
        className='text-a'
      />
      <br />
      <button className='btn' onClick={handleFetchData}>
        Fetch Data
      </button>
      {loading && <p>Loading...</p>}
      {data && (
        <div className='details'>
          <pre className='json-format'>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
