import React, { useState } from 'react';
import axios from 'axios';
import Display from './displayData';

export default function AddJson() {

    const [order_number, setOrderNumber] = useState('')

    const [stamp, setStamp] = useState('')

    const [status, setStatus] = useState('')

    const [responseData, setResponseData] = useState(null)

    const [showSuccessMessage, setShowSuccessMessage] = useState(false)


    const handleSubmit = async (e) => {

        console.log('sending at handlesubmit')

        e.preventDefault();

        try {

            const response = await axios.post('http://localhost:9000/aliens/send', {

                order_number,

                stamp,

                status

            });

            setResponseData(response.data)

            setShowSuccessMessage(true)

            console.log(response.data);



            // Hide success message after 3 seconds
            setTimeout(() => {

                setShowSuccessMessage(false);

            }, 3000)

        } catch (error) {

            console.log(error)
        }

    }

    return (

        <div>

            {showSuccessMessage && <p className='success-message'>Data added successfully!</p>}

            <form onSubmit={handleSubmit}>

                <div className='input-group'>
                    <label>orderNumber</label>
                    <input type="text"
                        value={order_number}
                        onChange={(e) => setOrderNumber(e.target.value)}
                    />
                </div>


                <div className='input-group'>
                    <label>Stamp</label>
                    <input type="string"
                        value={stamp}
                        onChange={(e) => setStamp(e.target.value)}
                    />
                </div>

                <div className='input-group'>
                    <label>Status</label>
                    <input type="string" value={status}
                        onChange={(e) => setStatus(e.target.value)}
                    />
                </div>

                <button type="submit">Submit</button>

            </form>
             <Display/>


            {responseData && (

                <div>

                    <table className="styled-table">

                        <thead>

                            <tr>
                                <th>ID</th>
                                <th>Order Number</th>
                                <th>Stamp</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>

                            {responseData.query.map((item) => (

                                <tr key={item.id}>

                                    <td>{item.id}</td>

                                    <td>{item.query_string.order_number}</td>

                                    <td>{item.query_string.stamp}</td>

                                    <td>{item.query_string.status}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    )
}
