import React, { useState } from 'react';
import axios from 'axios';

export default function GetData() {
  const [quoteNumber, setQuoteNumber] = useState('');
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleFetchData = () => {
    setLoading(true);
    setErrorMessage('');

    axios
      .get(`http://localhost:9000/aliens/quote?QUOTE_NUMBER=${quoteNumber}`)

      .then((response) => {
        if (response.status === 200) {
          setData(response.data);
        } else {
          setErrorMessage('Data not found.');
        }
      })
      .catch((error) => {
        console.log(error, 'Error');
        setErrorMessage('An error occurred while fetching data.');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className='container'>
      <label className='label' htmlFor='quoteNumber'>
        Quote Number:
      </label>
      <textarea
        type='text'
        value={quoteNumber}
        onChange={(e) => setQuoteNumber(e.target.value)}
        placeholder='Enter quote number'
        className='text-a'
      />
      <br />
      <button className='btn' onClick={handleFetchData}>
        Fetch Data
      </button>
      {loading && <p>Loading...</p>}
      {errorMessage && <p className='error-message'>{errorMessage}</p>}
      {data && (
        <div className='details'>
          <pre className='json-format'>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
