 import Display from './displayData';

import React, { useState } from 'react';

import axios from 'axios';

export default function AddJson() {

    const [query, setQuery] = useState('');

    const [showSuccessMessage, setShowSuccessMessage] = useState(false)

    const [responseData, setResponseData] = useState(null)



    const handleSubmit = async (e) => {

        console.log(handleSubmit,'section')

        e.preventDefault()


        try{
            const response = await axios.post('http://localhost:9000/aliens/dom',{

                query

            });

            console.log(response,'show example')
              setShowSuccessMessage(true)
              setQuery('');

             console.log(response.data);

        }catch(error){

         console.log(error)

        }

        setTimeout(() => {

            setShowSuccessMessage(false);

        }, 3000)


    };

    return (
        <div>
        {showSuccessMessage && <p className='success-message'>Data added successfully!</p>}

       <form onSubmit={handleSubmit}>

        <label>


            <input 

            type="string"

            value={query}

            onChange={(e)=> setQuery(e.target.value)}

            />

        </label>
        <br/>

        <button type="submit">Submit</button>


       </form>

       <Display/>



       </div>

    )

}
