import axios from 'axios';
import React, { useState } from 'react';

export default function SearchData() {

    const [query, setQuery] = useState('');

    const [data, setData] = useState(null);

    const [loading, setLoading] = useState(false);

    const [errorMessage, setErrorMessage] = useState('');

    const handleFetchData = () => {

        setLoading(true)

        setErrorMessage('')


        .get(`http://localhost:9000/aliens/search?q=${query}`)

            .then(response => {

                if (response.status === 200) {

                    setData(response.data)

                } else {

                    setErrorMessage('An error occure');
                }
            })

            .catch(error=>{

                console.log(error,"Error")

                setErrorMessage('Data Not Found')
            })

             .finally(() => {
                setLoading(false);
              });
    }

    return (

        <div className='container'>

        <label className='label' htmlFor='quoteNumber'>

          Quote Number:

        </label>

        <textarea

          type='text'

          value={quoteNumber}

          onChange={e => setQ(e.target.value)}

          placeholder='Enter quote number'

          className='text-a'

        />
        <br />

        <button className='btn' onClick={handleFetchData}>

          Fetch Data

        </button>

        {loading && <p>Loading...</p>}

        {errorMessage && <p className='error-message'>{errorMessage}</p>}

        {data && (

          <div className='details'>

            <pre className='json-format'>{JSON.stringify(data, null, 2)}</pre>

          </div>
        )}
      </div>
    )
}
