 import Display from './displayData';

import React, { useState } from 'react';

import axios from 'axios';

export default function AddJson() {

    const [quoteNumber, setQuery_Number] = useState('');
    const [showSuccessMessage, setShowSuccessMessage] = useState(false)



    const handleSubmit = async (e) => {
        e.preventDefault()


        try{
            const response = await axios.post('http://localhost:9000/aliens/send',{

                QUOTE_NUMBER: quoteNumber

            });
            console.log(response,'show example')



              setShowSuccessMessage(true)

console.log(response.data);

        }catch(error){

         console.log(error)

        }

        setTimeout(() => {

            setShowSuccessMessage(false);

        }, 3000)


    };

    return (
        <div>
              {showSuccessMessage && <p className='success-message'>Data added successfully!</p>}

       <form onSubmit={handleSubmit}>

        <label>

            Name:
            <input 

            type="string"

            value={quoteNumber}

            onChange={(e)=> setQuery_Number(e.target.value)}

            />

        </label>
        <br/>

        <button type="submit">Submit</button>


       </form>
       <Display/>


       </div>

    )

}
