import Display from './displayData';

import React, { useState } from 'react';

import axios from 'axios';

export default function AddJson() {

    const [quotenumber, setQuery_Number] = useState('');



    const handleSubmit = async (e) => {
        e.preventDefault()


        try{
            const response = await axios.post('http://localhost:9000/aliens/send',{

                quotenumber

            });
            console.log(response)

        }catch(error){

         console.log(error)

        }


    };



    return (

       <form onSubmit={handleSubmit}>
        <label>
            Name:
            <input 
            type="string"
            value={quotenumber}
            onChange={(e)=> setQuery_Number(e.target.value)}
            />
        </label>
        <br/>
        <button type="submit">Submit</button>


       </form>

    )

}
