import React, { useState } from 'react';

import axios from 'axios';

export default function AddJson() {
  const [quoteNumber, setQuoteNumber] = useState('');

  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const [responseData, setResponseData] = useState(null);

  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Check if the quote number is already in use
      const checkResponse = await axios.get('http://localhost:9000/aliens', {
        params: {
          QUOTE_NUMBER: quoteNumber
        }
      });
      if (checkResponse.data.length > 0) {
        setErrorMessage('The quote number is already in use');
        return;
      }

      const response = await axios.post('http://localhost:9000/aliens/send', {
        QUOTE_NUMBER: quoteNumber
      });

      console.log(response, 'show example');

      setShowSuccessMessage(true);
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }

    setTimeout(() => {
      setShowSuccessMessage(false);
    }, 3000);
  };

  return (
    <div>
      {showSuccessMessage && <p className='success-message'>Data added successfully!</p>}
      {errorMessage && <p className='error-message'>{errorMessage}</p>}

      <form onSubmit={handleSubmit}>
        <label>
          QUOTE_NUMBER
          <input
            type='string'
            value={quoteNumber}
            onChange={(e) => setQuoteNumber(e.target.value)}
          />
        </label>
        <br />
        <button type='submit'>Submit</button>
      </form>
      <Display />
    </div>
  );
}
