import React from 'React';
import useState from 'react';
import axios from 'axios';





export default function Search(){

const [orderNumber, setOrderNumber] = useState('');

const [data, setData] = useState('');

const [loading,setLoading] = useState(false);

const [errorMessage,setErrorMessage] = useState('');



const handleFetchData = async ()=>{

    setLoading(true)

    setData('')

    setErrorMessage('')

   try{
    const response = await axios.get(`http://localhost/aliens/send${orderNumber}`)
   if(response.status === 200){
    if(response.data){
        setData(response.data)
    }else{
        setErrorMessage("Data not found in mangodb")

    }

   }else{

    setErrorMessage("An error occured")

   }

   }catch(error){
    console.log(error)
   }
   finally {

    setLoading(false);

  }

}


    return(

    <div className='container'>

    <label className='label' htmlFor='orderNumber'>

      Order Number:

    </label>

    <textarea

      type='text'

      value={orderNumber}

      onChange={(e) => setOrderNumber(e.target.value)}

      placeholder='Enter order number'

      className='text-a'

    />

    <br />

    <button className='btn' onClick={handleFetchData}>

      Fetch Data

    </button>

    {loading && <p>Loading...</p>}

    {errorMessage && <p className='error-message'>{errorMessage}</p>}

    {data && (

      <div className='details'>

        {/* Separate div for displaying details */}

        <pre className='json-format'>{JSON.stringify(data, null, 2)}</pre>

      </div>

    )}

  </div>

    )
}
