import React, { useState } from 'react';
import axios from 'axios';

export default function Format() {
  const [key, setKey] = useState('');
  const [data, setData] = useState(null);
  const [con, setCon] = useState('');
  const [loading, setLoading] = useState(false);
  const [query,setQuery] = useState('')

  const handleFetchData = () => {
    setLoading(true);

    axios
      .get(`http://localhost:9000/aliens/search?q=${query}`)
      .then((response) => {
        if (response.status === 200) {
          const formattedData = response.data;
          console.log(formattedData);

          formattedData.query.forEach((queryItem) => {
            if (queryItem.query_string && queryItem.query_string.key) {
              queryItem.query_string.key += ` ${con}`; // Update the key field with the value of 'con'
            }
          });

          setData(formattedData);
        } else {
          alert('An error occurred');
        }
      })
      .catch((error) => {
        console.log('Error', error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className="container">
      <label className="label" htmlFor="key">
        Key:
      </label>
      <textarea
        type="String"
        value={key}
        onChange={(e) => setKey(e.target.value)}
        placeholder="Enter key"
        className="text-input"
      />
      <textarea
        type="String"
        value={con}
        onChange={(e) => setCon(e.target.value)}
        className="text-input"
      />
      <button className="btn" onClick={handleFetchData}>
        JSON Format
      </button>

      {loading && <p>Loading...</p>}

      {data && (
        <div className="details">
          <pre className="json-format">{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
