import React, { useState } from 'react';
import axios from 'axios';

export default function GetData() {
  const [quoteNumber, setQuoteNumber] = useState('');
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleFetchData = () => {

    setLoading(true);

    setErrorMessage('');

    axios
      .get(`http://localhost:9000/aliens/quote`, {
        params: {
          QUOTE_NUMBER: quoteNumber,
        },
      })
      .then((response) => {
        if (response.status === 200 && response.data) {
          setData(response.data);
        } else {
          setErrorMessage('Data not found.');
        }
      })

      .catch((error) => {

        console.error(error);

        setErrorMessage('An error occurred while fetching data.');

      })
      .finally(() => {
        setLoading(false);
      });
  };

  const handleSendData = () => {

    setLoading(true);
    setErrorMessage('');

    axios
      .post('http://localhost:9000/aliens/send', {

        QUOTE_NUMBER: quoteNumber,
      })
      .then((response) => {
        if (response.status === 200 && response.data) {
          setData(response.data);
        } else {
          setErrorMessage('Failed to send data.');
        }
      })
      .catch((error) => {

        console.error(error);

        setErrorMessage('An error occurred while sending data.');

      })
      .finally(() => {

        setLoading(false);

      });
  };

  return (
    <div className="container">
      <label className="label" htmlFor="quoteNumber">
        Quote Number:
      </label>
      <input
        type="text"
        value={quoteNumber}
        onChange={(e) => setQuoteNumber(e.target.value)}
        placeholder="Enter quote number"
        className="text-input"
      />
      <br />

      <button onClick={handleSendData}>Send Data</button>
      {loading && <p>Loading...</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {data && (
        <div className="details">
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
