import React, { useState } from 'react';
import axios from 'axios';

e
export default function OrderForm() {

  const [searchInput, setSearchInput] = useState('');

  const [loading, setLoading] = useState(false);

  const [data, setData] = useState([]);


  const handleFetchData = async () => {

    setLoading(true);

    try {

      const query = searchInput.split(' ')[0]; // Extract the search key

      const concordinateValue = searchInput.substring(query.length).trim(); // Extract the concordinate value

      const response = await axios.get(`http://localhost:2000/formats/query?q=${query}`);

      const responseFormatted = response.data.map((item) => {

        const concordinate = item.query[0].query_string.query + ' ' + concordinateValue;

        return {

          ...item,

          query: [

            {
              ...item.query[0],

              query_string: {

                query: concordinate,

              },

            },
          ],
        };
      });

      setData(responseFormatted);

    } catch (error) {
      console.log(error, 'error');

    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <textarea
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
        placeholder="Search the key and concordinate value"
      />

      <button onClick={handleFetchData}>Search</button>

      {loading && <p>Loading...</p>}

      {Array.isArray(data) && (
        <div>
          {data.map((item) => (
            <div key={item.id}>
              <pre>{JSON.stringify(item, null, 2)}</pre>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
