import React, { useState } from 'react';
import axios from 'axios';

export default function Format() {
  const [query, setQuery] = useState('');
  const [content, setContent] = useState('');
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFetchData = () => {
    setLoading(true);

    const formattedQuery = query.trim() !== '' ? `ORDER_NUMBER ${query}` : '';

    axios
      .get(`http://localhost:9000/aliens/search?q=${formattedQuery}`)
      .then((response) => {
        if (response.status === 200) {
          const formattedData = response.data.map((item) => ({
            ...item,
            query: item.query.map((queryItem) => ({
              ...queryItem,
              query_string: {
                ...queryItem.query_string,
                content: content.trim() !== '' ? `${content}` : '',
              },
            })),
          }));
          setData(formattedData);
        } else {
          alert('An error occurred');
        }
      })
      .catch((error) => {
        console.log('Error', error);
      })
      .finally(() => {
        setLoading(false);
      });
  };
  return (
    <div className="container">
      <label htmlFor="query">Query:</label>
      <input
        type="text"
        id="query"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <label htmlFor="content">Additional Content:</label>
      <input
        type="text"
        id="content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
      />

      <button onClick={handleFetchData}>Fetch Data</button>

      {loading && <p>Loading...</p>}

      {data && (
        <div className="details">
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
