import Display from './displayData';

import React, { useState } from 'react';
import axios from 'axios';

export default function AddJson() {
  const [quote_number, setQuoteNumber] = useState('');
  const [responseData, setResponseData] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:9000/aliens/send', {
        quote_number,
      });

      setResponseData(response.data);
      setShowSuccessMessage(true);

      setTimeout(() => {
        setShowSuccessMessage(false);
      }, 3000);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      {showSuccessMessage && <p className='success-message'>Data added successfully!</p>}

      <form onSubmit={handleSubmit}>
        <div className='input-group'>
          <label>Quote Number</label>
          <input
            type='text'
            value={quote_number}
            onChange={(e) => setQuoteNumber(e.target.value)}
          />
        </div>

        <button type='submit'>Submit</button>
      </form>

      {responseData && (
        <div>
          <table className='styled-table'>
            <thead>
              <tr>
                <th>ID</th>
                <th>Quote Number</th>
              </tr>
            </thead>
            <tbody>
              {responseData.query.map((item) => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.query_string.quote_number}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
