import React from 'react';
import { useState } from 'react';
import axios from 'axios';


export default function QueryString(){

 const [quoteNumber , setQuoteNumber] = useState('');

 const [showSuccessMessage, setShowSuccessMessage] = useState('');

 const [responseData,setResponseData] = useState(null);

 const [errorMessage,setErrorMessage] = useState('')

 const handleSubmit = async (e)=>{
    e.preventDefault();

    try{

        const checkresponse = await axios.get('http://localhost:9000/aliens',{
            params:{
              QUOTE_NUMBER: quoteNumber  
            }
        });

        if(checkresponse.data.length > 0){

            setErrorMessage('The quote number is already in use');
            return;
        }

        const response = await axios.post('http://localhost:9000/aliens/send',{

            QUOTE_NUMBER: quoteNumber
        });
        console.log(response,' display details')

        setShowSuccessMessage(true)

    }catch(error){
        console.log(error,'Error')
    }
    setTimeout(()=>{

        setShowSuccessMessage(false);

    },3000)

 }

    return(

        <div>
           {showSuccessMessage && <p className='succes-message'>Data added successfully</p>}

           {errorMessage && <p className='error-message'>{errorMessage}</p>}

          <form onSubmit={handleSubmit}>
            <label>
                QUOTE_NUMBER
                <input/>
            </label>


          </form>
        </div>
    )
}
