import React, { useState } from 'react';
import axios from 'axios';

export default function OrderForm() {

  const [query, setQuery] = useState('');

  const [data, setData] = useState(null);

  const [loading, setLoading] = useState(false);

  const [concatValue, setConcatValue] = useState('');

  const handleSearch = () => {

    setLoading(true);

    axios
      .get(`http://localhost:9000/aliens/search?q=${query}`)

      .then((response) => {
        const formattedData = response.data.map((item) => {
          const concatenatedQuery = item.query[0].query_string.query + ' ' + concatValue;
          return {
            ...item,
            query: [
              {
                ...item.query[0],
                query_string: {
                  query: concatenatedQuery,
                },
              },
            ],
          };
        });

        setData(formattedData);
      })
      .catch((error) => {
        console.error(error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div>
      <textarea
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter search query"
      />
      <textarea
        type="text"
        value={concatValue}
        onChange={(e) => setConcatValue(e.target.value)}
        placeholder="Enter value to concatenate"
      />
      <button onClick={handleSearch}>Search</button>

      {loading && <p>Loading...</p>}

      {data && (
        <div>
          {data.map((item) => (

            <div key={item.id}>

              <pre>{JSON.stringify(item, null, 2)}</pre>

            </div>
          ))}
        </div>
      )}
    </div>
  );
}
